import React, { useState, useEffect } from 'react';
import { Timer, Play, Pause, RotateCcw } from 'lucide-react';

export default function PomodoroTimer() {
  const [workDuration, setWorkDuration] = useState(25);
  const [breakDuration, setBreakDuration] = useState(5);
  const [timeLeft, setTimeLeft] = useState(25 * 60);
  const [isRunning, setIsRunning] = useState(false);
  const [isWorkTime, setIsWorkTime] = useState(true);

  useEffect(() => {
    let intervalId;
    if (isRunning && timeLeft > 0) {
      intervalId = setInterval(() => {
        setTimeLeft((prevTime) => prevTime - 1);
      }, 1000);
    } else if (timeLeft === 0) {
      // Switch between work and break time
      setIsWorkTime(!isWorkTime);
      setTimeLeft(isWorkTime ? breakDuration * 60 : workDuration * 60);
      // Play notification sound when timer ends
      new Audio('data:audio/wav;base64,').play();
    }
    return () => clearInterval(intervalId);
  }, [isRunning, timeLeft, isWorkTime]);

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  const handleStartPause = () => {
    setIsRunning(!isRunning);
  };

  const handleReset = () => {
    setIsRunning(false);
    setTimeLeft(workDuration * 60);
    setIsWorkTime(true);
  };

  const handleWorkDurationChange = (e) => {
    const value = parseInt(e.target.value) || 1;
    setWorkDuration(value);
    if (isWorkTime && !isRunning) {
      setTimeLeft(value * 60);
    }
  };

  const handleBreakDurationChange = (e) => {
    const value = parseInt(e.target.value) || 1;
    setBreakDuration(value);
    if (!isWorkTime && !isRunning) {
      setTimeLeft(value * 60);
    }
  };

  return (
    <div className="flex flex-col h-full items-center justify-center space-y-8">
      <div className="flex space-x-4 mb-4">
        <div>
          <label className="block text-sm font-medium text-black mb-1">Work Duration (min)</label>
          <input
            type="number"
            min="1"
            value={workDuration}
            onChange={handleWorkDurationChange}
            className="w-24 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-gray-900"
            disabled={isRunning}
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-black mb-1">Break Duration (min)</label>
          <input
            type="number"
            min="1"
            value={breakDuration}
            onChange={handleBreakDurationChange}
            className="w-24 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-gray-900"
            disabled={isRunning}
          />
        </div>
      </div>
      <div className="text-center">
        <h2 className="text-2xl font-semibold text-black mb-2">
          {isWorkTime ? 'Work Time' : 'Break Time'}
        </h2>
        <div className="text-6xl font-bold text-black mb-8">
          {formatTime(timeLeft)}
        </div>
      </div>

      <div className="flex space-x-4">
        <button
          onClick={handleStartPause}
          className="flex items-center space-x-2 px-6 py-3 bg-black text-white rounded-lg hover:bg-gray-800 transition-colors"
        >
          {isRunning ? (
            <>
              <Pause className="w-5 h-5" />
              <span>Pause</span>
            </>
          ) : (
            <>
              <Play className="w-5 h-5" />
              <span>Start</span>
            </>
          )}
        </button>

        <button
          onClick={handleReset}
          className="flex items-center space-x-2 px-6 py-3 border border-gray-200 rounded-lg hover:bg-gray-50 transition-colors"
        >
          <RotateCcw className="w-5 h-5" />
          <span>Reset</span>
        </button>
      </div>
    </div>
  );
}